    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) { 
    	String year = jTextField1.getText();
    	int number = Integer.parseInt(year);
    	String coef = jTextField2.getText();
    	App app = new App();
        ArrayList<String> list = app.newCost(number, coef);
        String s = "";
        for(String si:list) {
        	s += si;
		}
        String out = String.format("| %-2s | %-100s | %-10s %n", "id","book_name","new_price");
        jTextArea1.setText(out);
        jTextArea1.setText(s);
    }                                        

