    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
    	App app = new App();
    	String book_name = jTextField1.getText();
    	if(app.checkBook(book_name) == true) {
    	jLabel3.setForeground(new java.awt.Color(69, 228, 13));
    	jLabel3.setText("В наявності");
    	}else {
    		jLabel3.setForeground(new java.awt.Color(210, 4, 45));
    		jLabel3.setText("Не в наявності");
    	}
        
    }//GEN-LAST:event_jButton1ActionPerformed

